import { useState, createContext, useEffect } from "react";
import { get } from "./aux_api";
export const Context = createContext();

/*Componente que se comparte en toda la aplicación. 
Hacemos un get de todos los coches para compartir en todos los componentes
*/
export function ContextProvider({ children }) {

    const [sharedState, setSharedState] = useState(
        get("http://localhost:3000/api" + "/allCoches/").then()

    );

    /* async function getCoches() {
         const users = await get("http://localhost:3000/api" + "/allCoches/");
         setSharedContext(users)
     }*/

    const actions = {
        setState: setSharedState,
    }

    const [sharedContext, setSharedContext] = useState({
        state: sharedState,
        actions
    })

    useEffect(
        () => {
            setSharedContext({
                state: sharedState,
                actions
            })
        },
        [sharedState]
    )
    

    function getCars() {
        get("http://localhost:3000/api" + "/allCoches/").then(
            data => setCoches(data.map(
                (element) =>
                    <>
                        <h3>{element.marca}</h3>
                        <p>{element.precio}</p>
                    </>
            ))
        )
    }

    return (
        <Context.Provider value={sharedContext}>
            {children}
        </Context.Provider>
    )
}
