import { useEffect, useState, useRef } from 'react';
import CarCarrousel from './components/CarCarrousel/CarCarrousel';
import Navbar from 'react-bootstrap/Navbar';
import './App.css';
import NavBar from './components/NavBar/NavBar';
import IndividualPage from './components/IndividualPage/IndividualPage';
import CarCarrousels from './components/CarCarrousels/CarCarrousels';
import { Route, Routes } from 'react-router-dom';
import { useContext } from "react"
import { Context } from "./SharedState"

function App() {

  const ruta = "http://127.0.0.1:5500/project-react/src/img/";

  const { state } = useContext(Context);

  const [vehiculosArray, setVehiculosArray] = useState([
    {
      marca: "Opel",
      modelo: "Corsa",
      precio: "12.900 €",
      km: "5000 km",
      img: ruta + "opelAstra.jpg",
      venta: true
    },
    {
      marca: "Ford",
      modelo: "Mondeo",
      precio: "15.900 €",
      km: "8500 km",
      img: ruta + "fordMondeo.jpg",
      venta: true
    },
    {
      marca: "Peugeot",
      modelo: "408",
      precio: "8.900 €",
      km: "2500 km",
      img: ruta + "peugeotPartner.jpg",
      venta: true
    },
    {
      marca: "Peugeot",
      modelo: "Partner",
      precio: "8.900 €",
      km: "2500 km",
      img: ruta + "peugeotPartner.jpg",
      venta: false
    }
    ,
    {
      marca: "Nissan",
      modelo: "Qashqai",
      precio: "10.900 €",
      km: "5500 km",
      img: ruta + "nissanQashqai.jpg",
      venta: true
    },
    {
      marca: "Peugeot",
      modelo: "2008",
      precio: "12.900 €",
      km: "1500 km",
      img: ruta + "peugeot2008.jpg",
      venta: false
    },
    {
      marca: "Seat",
      modelo: "Ibiza",
      precio: "5.900 €",
      km: "2500 km",
      img: ruta + "seatIbiza.jpg",
      venta: false
    },
    {
      marca: "Seat",
      modelo: "Arona",
      precio: "5.900 €",
      km: "2500 km",
      img: ruta + "seatIbiza.jpg",
      venta: false
    },
    {
      marca: "VolksWagen",
      modelo: "Polo",
      precio: "7.900 €",
      km: "10500 km",
      img: ruta + "vwPolo.jpg",
      venta: false
    }
  ]);


  return (
    <>
      <h1>Concesionario Vioño</h1>
      <NavBar vehiculos={vehiculosArray} />


      <Routes>
        <Route path="/" element={<CarCarrousels vehiculos={vehiculosArray} />} />
        <Route path="/vehiculo/" element={<IndividualPage vehiculo={vehiculosArray} />}>
          <Route path=":id" element={<IndividualPage vehiculo={vehiculosArray["vehiculo/"]} />} />
        </Route>
      </Routes>
    </>

  );
}

export default App;
