import './App.css';
import NavBar from './components/NavBar/NavBar';
import IndividualPage from './components/IndividualPage/IndividualPage';
import CarCarrousels from './components/CarCarrousels/CarCarrousels';
import { Route, Routes } from 'react-router-dom';
import { useContext } from "react"
import { Context } from "./SharedState"

function App() {

  const ruta = "http://127.0.0.1:5500/02_frontend/src/img/";

  const { state, actions } = useContext(Context);

  useEffect(
    () => {
        getCars()  
    },

    []
)

function getCars() {
    get("http://localhost:3000/api" + "/allCoches/").then(
        data => setSharedState(data.map(
            (element) =>
                <>
                    <h3>{element.marca}</h3>
                    <p>{element.precio}</p>
                </>
        ))
    )
}

  const vehiculosArray = [
    {
      marca: "Opel",
      modelo: "Corsa",
      precio: "12.900 €",
      km: "5000 km",
      img: ruta + "opelAstra.jpg",
      venta: true
    },
    {
      marca: "Ford",
      modelo: "Mondeo",
      precio: "15.900 €",
      km: "8500 km",
      img: ruta + "fordMondeo.jpg",
      venta: true
    },
    {
      marca: "Peugeot",
      modelo: "408",
      precio: "8.900 €",
      km: "2500 km",
      img: ruta + "peugeotPartner.jpg",
      venta: true
    },
    {
      marca: "Peugeot",
      modelo: "Partner",
      precio: "8.900 €",
      km: "2500 km",
      img: ruta + "peugeotPartner.jpg",
      venta: false
    }
    ,
    {
      marca: "Nissan",
      modelo: "Qashqai",
      precio: "10.900 €",
      km: "5500 km",
      img: ruta + "nissanQashqai.jpg",
      venta: true
    },
    {
      marca: "Peugeot",
      modelo: "2008",
      precio: "12.900 €",
      km: "1500 km",
      img: ruta + "peugeot2008.jpg",
      venta: false
    },
    {
      marca: "Seat",
      modelo: "Ibiza",
      precio: "5.900 €",
      km: "2500 km",
      img: ruta + "seatIbiza.jpg",
      venta: false
    },
    {
      marca: "Seat",
      modelo: "Arona",
      precio: "5.900 €",
      km: "2500 km",
      img: ruta + "seatIbiza.jpg",
      venta: false
    },
    {
      marca: "VolksWagen",
      modelo: "Polo",
      precio: "7.900 €",
      km: "10500 km",
      img: ruta + "vwPolo.jpg",
      venta: false
    }
  ];


  return (
    <>
      <h1>Concesionario Vioño</h1>
      <NavBar vehiculos={vehiculosArray} />
      <Routes>
        <Route path="/" element={<CarCarrousels vehiculos={vehiculosArray} />} />
        /*Añadir un nuevo parametro conforme si es venta o no*/
        <Route path="/vehiculo/:id" element={<IndividualPage vehiculos={vehiculosArray} />} />
      </Routes>
    </>

  );
}

export default App;
